// Code to make E/p vs E, E/p vs p, and E/p vs t plots// Using either gain corrected, or uncorrected// It also performs gauss fits in Y to slices in X// Sam Grant // March 2019// samuel.grant.18@ucl.ac.uk#include <iostream>#include "FitGaussSlices.h"#include "CMAP.h"#include "TFile.h"#include "TCanvas.h"#include "TH1D.h"#include "TH2D.h"#include "TStyle.h"#include "TAxis.h"#include "TProfile.h"#include "TF1.h"#include "TDirectory.h"#include "TObject.h"#include "TGraph.h"#include "TLegend.h"#include "TPaveStats.h"using namespace std;int main() {  bool save = false;  //  string inputFname = "../ReadNTuples/RootFiles/RawEpXtalFid.root";  string inputFname = "../ReadNTuples/RootFiles/RawEpXtal_9day.root";  string outputFname = "RootFiles/PlotsEpXtal_9day.root";    TFile *input = TFile::Open(inputFname.c_str());  TFile *output = new TFile(outputFname.c_str(), "RECREATE");  cout << "Reading: " << inputFname << endl;  //Station loop  for (int calo = 13 ; calo < 20 ; calo = calo + 6 ) {     string stn = "St"+to_string(calo)+"_";    string tstn = "Stn "+to_string(calo-1);    string ffoo = "Ep_vs_xtal";    //    string tfoo = "Stn E/p vs";    string afoo = ";Crystal;E/p";	    // Put strings together for iterting hist/file/title names    string h = stn+ffoo;    string cmap_fname = stn+"cmap_"+ffoo;    string fit_fname = stn+ffoo;    string g_fname = stn+"gauss_"+ffoo;    string title = tstn+afoo;    //cout<<h<<endl;    TH2D *Ep_vs_xtal = (TH2D*)input->Get(h.c_str());    if (Ep_vs_xtal == 0) continue;    CMAP(Ep_vs_xtal, title, cmap_fname, output, save);    FitGaussSlices(Ep_vs_xtal, title, fit_fname, g_fname, output, calo, save);  }    output->Write();  output->Close();  input->Close();  cout << "\nCreated: " << outputFname << endl;  return 0;}