// Code to make E/p vs E, E/p vs p, and E/p vs t plots// Using either gain corrected, or uncorrected// It also performs gauss fits in Y to slices in X// Sam Grant // March 2019// samuel.grant.18@ucl.ac.uk#include <iostream>#include "FitGaussSlices.h"#include "CMAP.h"#include "TFile.h"#include "TCanvas.h"#include "TH1D.h"#include "TH2D.h"#include "TStyle.h"#include "TAxis.h"#include "TProfile.h"#include "TF1.h"#include "TDirectory.h"#include "TObject.h"#include "TGraph.h"#include "TLegend.h"#include "TPaveStats.h"using namespace std;int main() {  //    string suffix = "_skip18";  string suffix = "";  bool save = false;    string inputFname = "../Read/RootFiles/allMuons"+suffix+".root";  string outputFname = "RootFiles/plots_allMuons"+suffix+".root";    TFile *input = TFile::Open(inputFname.c_str());  TFile *output = new TFile(outputFname.c_str(), "RECREATE");  cout << "Reading: " << input << endl;  //Station loop  for (int calo = 13 ; calo < 20 ; calo = calo + 6 ) {     string stn = "St"+to_string(calo)+"_";    string tstn = "Calo "+to_string(calo)+" | ";    string ffoo = "Ep_vs_t";    string tfoo = "E/p vs t";    string afoo = ";In Fill Time [#mus];E/p";		for (int xtal = 0 ; xtal < 54 ; xtal++) {	  // Put strings together for iterting hist/file/title names	  string h = stn+ffoo+"_"+to_string(xtal);	  string cmap_fname = stn+"cmap_"+ffoo+"_"+to_string(xtal);	  string fit_fname = stn+ffoo+"_"+to_string(xtal);	  string g_fname = stn+"gauss_"+ffoo+"_"+to_string(xtal);	  string title = tstn+tfoo+afoo;	  //cout<<h<<endl;	  TH2D *Ep_vs_t = (TH2D*)input->Get(h.c_str());	  //	  if(Ep_vs_t != 0) cout<<h<<endl;       	  if (Ep_vs_t == 0) continue;	  Ep_vs_t->GetXaxis()->SetLimits(0,4.2*50);	  //	  CMAP(Ep_vs_t, title, cmap_fname, output, save);	  FitGaussSlices(Ep_vs_t, title, fit_fname, g_fname, output, save);	}  }    output->Write();  output->Close();  input->Close();  cout << "\nCreated: " << outputFname << endl;  return 0;}