// Code to make E/p vs E, E/p vs p, and E/p vs t plots// Using either gain corrected, or uncorrected// It also performs gauss fits in Y to slices in X// Sam Grant // March 2019// samuel.grant.18@ucl.ac.uk#include <iostream>#include "TFile.h"#include "TCanvas.h"#include "TH1D.h"#include "TH2D.h"#include "TStyle.h"#include "TAxis.h"#include "TProfile.h"#include "TF1.h"#include "TDirectory.h"#include "TObject.h"#include "TGraph.h"#include "TLegend.h"#include "TPaveStats.h"using namespace std;// Colour map plottervoid cmap(TH2D *hist, string title, string fname, string name, TFile *output, bool save) {  //Clone to stop bugs   TH2D *hist_clone = (TH2D*)hist->Clone("hist_clone");  //Force the ranges to be sensible if ROOT's stupid autoscale fails like normal  double xmin = hist_clone->GetXaxis()->GetBinCenter( hist_clone -> FindFirstBinAbove(0,1) );  double xmax = hist_clone->GetXaxis()->GetBinCenter( hist_clone -> FindLastBinAbove(0,1) );  hist_clone->GetXaxis()->SetRangeUser(xmin,xmax);  double ymin = hist_clone->GetYaxis()->GetBinCenter( hist_clone -> FindFirstBinAbove(0,2) );  double ymax = hist_clone->GetYaxis()->GetBinCenter( hist_clone -> FindLastBinAbove(0,2) );  hist_clone->GetYaxis()->SetRangeUser(ymin,ymax);  hist_clone->SetName(name.c_str());  TCanvas *c = new TCanvas("c", "c", 2000, 1000);  gStyle->SetPalette(55);    hist_clone->SetStats(0);  hist_clone->SetTitle(title.c_str());  c->SetRightMargin(0.13);  hist_clone->SetOption("COLZ");  gStyle->SetPalette(55);    hist_clone->Draw();  if (save) {    c->SaveAs(fname.c_str());   }  hist_clone->SetDirectory(output);  delete c;  return;}// Fit gaussians to slices along the x-axis and plot their meansvoid fitSlicesGaus(TH2D *hist, string title, string fname,string g_fname, string name, TFile *output, bool save) {  //Clone to stop bugs   TH2D *hist_clone = (TH2D*)hist->Clone("hist_clone");  int nBins = hist_clone->GetNbinsX();   // Hardcode this to change the number of slices  int nSlices = nBins;  // Get the length of the slice in x  int length = nBins / nSlices;  hist_clone -> RebinX(length);  // Define the gaussian   TF1 *gFunc = new TF1("gFunc", "gaus");  // Declare step edges  int loStep;  int hiStep;  // Book a 1D hist for the Y projection slices  TH1D *projY;  // Book a 1D hist to take the results of each fit on it's X axis  TH1D *projX = hist_clone -> ProjectionX("prX");  // projX -> Rebin(length);   projX -> Reset();  // Threshold (minimum number of bins), to define the fit range  double threshold;  double fitMin;  double fitMax;  double binCentre;  double centre;  // Slice loop  /*you could use Rebin2D(1, length) to rebin the 2D plot, then just make the projY between (i+1 and i+1) */  for(int i = 0 ; i < nSlices; i++) {    // Define steps    loStep = i+1;//*length;    hiStep = i+1;//*length;    // Perform projection    projY = hist_clone->ProjectionY("prY",loStep,hiStep);    // Clean up empty bins     if (projY->GetEntries() < 1) continue;    //Define the threshold, trying to avoid those tails. Half maximum works. Hacky but never mind.    threshold =  (projY -> GetBinContent(projY->GetMaximumBin()));// * (2/3);    threshold = threshold * 0.5;    // Define the fit range    fitMin = projY -> GetBinCenter(projY -> FindFirstBinAbove(threshold,1));    fitMax = projY -> GetBinCenter(projY -> FindLastBinAbove(threshold,1));    // "Q" : supress printing "M" use minuit to improve fit result        // Fill a histogram with the fit results    projX -> SetBinContent(i+1, gFunc->GetParameter(1));    projX -> SetBinError(i+1, gFunc->GetParError(1));    // Draw fits to slices, to see what's going on    // binCentre = projY->GetMaximumBin();    //  centre = projY->GetBinCenter(binCentre);    // gFunc->SetParameter(1,centre);    projY -> Fit(gFunc,"RQ","",fitMin,fitMax);    TCanvas *c1 = new TCanvas();    //ksiourmen    gStyle->SetOptFit();    gStyle->SetOptStat(112210);    projY->SetMarkerColor(kBlack);    projY->SetLineColor(kBlack);    projY->Draw();    projY->SetName(("gauss_"+name+"_"+to_string(i)).c_str());    projY->SetDirectory(output);    if (save) {      c1->SaveAs((g_fname+"_"+to_string(i)+".png").c_str());    }    delete c1;    //  delete projY;  } // End slice loop  //Force the ranges to be sensible if ROOT's autoscale fails  int binmin = projX->FindFirstBinAbove(0,1);  //  binmin = binmin - 1;  int binmax = projX->FindLastBinAbove(0,1);  //  double xmin = (projX -> GetBinCenter(binmin));;//projX->FindFirstBinAbove(0,1)-1) );  // double xmax = projX -> GetBinCenter(binmax);//projX->FindLastBinAbove(0,1)+1) );  // Draw the means, and compare with the profile   projX->SetName(name.c_str());  // tpf->SetName("X Profile");  TProfile *tp = hist_clone->  TCanvas *c2 = new TCanvas("c2", "c2", 2000, 1000);  gStyle->SetOptStat(110010);  //projX->Draw();  projX->SetTitle(title.c_str());  projX->SetMarkerColor(kBlack);  projX->SetLineColor(kBlack);  projX->GetXaxis()->SetRange(binmin,binmax);//xmin,xmax);  projX->DrawCopy();  if (save) {    c2->SaveAs(fname.c_str());  }   projX->SetDirectory(output);  delete c2;  // delete projY;  // delete projX;  //  delete gFunc;  return;}int main() {  // Open output file   string output_fname;  cout << "\ngainPlotter.C generates E/p plots using cluster matched nutples, and then fits slices in x with gaussians.\n"<<endl;  // Take input for gain corrected or uncorrected  bool gainCorr, tcut, save;  string input1, input2, input3;  cout << "Time cut applied? Yes (y) or no (n): ";  cin >> input2;  if (input2 == "y") { tcut = true; }  else if (input2 == "n" ) { tcut = false; }  else {    cout << "Error, try again" << endl;    return 0;  }  // Pick the corresponding file  string whichFile;  if (!tcut) {    whichFile = "../uncorrected/merged_uncorrected_new_B.root";    output_fname = "gain_uncorrected_new_B.root";  }  else if (tcut) {     //      whichFile = "../uncorrected/merged_uncorrected2.root";     // output_fname = "gain_uncorrected_no_tcut.root";    whichFile = "../uncorrected/merged_uncorrected_new_D.root";     output_fname = "gain_uncorrected_new_D.root";   }   TFile *output = new TFile(output_fname.c_str(), "recreate");  // Open it  TFile *input = TFile::Open(whichFile.c_str());  cout << "Reading: " << whichFile << endl;  string name = "e^{+}, High Flux";     // Start fiducial region loop  for (int l = 0 ; l < 3 ; l++ ) {	    // Define all the strings needed for filenames, titles, booking histograms    string prefix, dir0, dir1, dir2, fid;    string fmat1 = ".png";    string fmat2 = ".pdf";     string title[8], h[8], fit_fname[8], cmap_fname[8], g_fname[8];    string ffoo[8] = {"E_vs_p_","Ep_vs_E_","Ep_vs_p_","Ep_vs_t_","Ep_vs_t_early_","xy_calo_","Ep_vs_x_","Ep_vs_y_"};    string tfoo[8] = {"E vs p, ","E/p vs E, " ,"E/p vs p, ","E/p vs t, ","E/p vs t (early), ","XY Calo, ","E/p vs Calo X, ","E/p vs Calo Y, "};    string afoo[8] = {";Track Momentum [GeV];Energy in Calo [GeV]",";Energy in Calo [GeV];E/p", ";Track Momentum [GeV];E/p",";Absolute Time [#mus];E/p",";Absolute Time [#mus];E/p",";X Calo Decay Position [mm];Y Calo Decay Position [mm]",";X Calo Decay Position [mm];E/p",";Y Calo Decay Position [mm];E/p"};    // For titles    if (l == 0) {      fid = "";    }    else if (l == 1 ) {       cout << fid << endl;      //  ffid = "fid";      fid = ", Fiducial";    }     else if (l == 2 ) {       cout << fid << endl;      // ffid = "nonFid";      fid = ", Non Fiducial";    }    int j = 3;    // Put strings together for iterting hist/file/title names    for  (int i = 0 ; i < 8 ; i++ ) {      h[i] = dir0+ffoo[i]+to_string(j)+"_"+to_string(l);      cmap_fname[i] = dir1+"cmap_"+ffoo[i]+to_string(j)+to_string(l)+fmat1;      // if (i < 6) {      fit_fname[i] = dir1+"fit_"+ffoo[i]+to_string(j)+to_string(l)+fmat1;      g_fname[i] = dir2+"gauss_"+ffoo[i]+to_string(j)+to_string(l);      //  }       //	  if (l==0) {      //  title[i] = prefix+tfoo[i]+name+afoo[i];      // }      // else {      title[i] = prefix+tfoo[i]+name+fid+afoo[i];      // }    }    /////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////    // Book the 2D histograms    TH2D *Ep = (TH2D*)input->Get(h[0].c_str());    TH2D *E = (TH2D*)input->Get(h[1].c_str());    TH2D *p = (TH2D*)input->Get(h[2].c_str());    TH2D *t = (TH2D*)input->Get(h[3].c_str());    TH2D *t_early = (TH2D*)input->Get(h[4].c_str());    TH2D *xy_calo = (TH2D*)input->Get(h[5].c_str());    TH2D *Ep_vs_x = (TH2D*)input->Get(h[6].c_str());    TH2D *Ep_vs_y = (TH2D*)input->Get(h[7].c_str());    Ep->GetXaxis()->SetLimits(0,4);    Ep->GetYaxis()->SetLimits(0,4);    E->GetXaxis()->SetLimits(0,4);    p->GetXaxis()->SetLimits(0,4);    t->GetXaxis()->SetLimits(0,4.2*1500);    t_early->GetXaxis()->SetLimits(0,4.2*50);    //	if(j != 3) continue;           cmap(Ep,title[0],cmap_fname[0],"cmap_"+ffoo[0]+to_string(j)+to_string(l), output, save);    cmap(E,title[1],cmap_fname[1],"cmap_"+ffoo[1]+to_string(j)+to_string(l), output, save);    cmap(p,title[2],cmap_fname[2],"cmap_"+ffoo[2]+to_string(j)+to_string(l),output, save);    cmap(t,title[3],cmap_fname[3],"cmap_"+ffoo[3]+to_string(j)+to_string(l), output, save);    cmap(t_early,title[4],cmap_fname[4],"cmap_"+ffoo[4]+to_string(j)+to_string(l), output, save);    cmap(xy_calo,title[5],cmap_fname[5],"cmap_"+ffoo[5]+to_string(j)+to_string(l), output, save);     cmap(Ep_vs_x,title[6],cmap_fname[6],"cmap_"+ffoo[6]+to_string(j)+to_string(l), output, save);    cmap(Ep_vs_y,title[7],cmap_fname[7],"cmap_"+ffoo[7]+to_string(j)+to_string(l), output, save); 	    fitSlicesGaus(Ep,title[0],fit_fname[0],g_fname[0],"fit_"+ffoo[0]+to_string(j)+to_string(l),output, save);     fitSlicesGaus(E,title[1],fit_fname[1],g_fname[1],"fit_"+ffoo[1]+to_string(j)+to_string(l),output, save);//,kBlue+2);    fitSlicesGaus(p,title[2],fit_fname[2],g_fname[2],"fit_"+ffoo[2]+to_string(j)+to_string(l),output, save);//,kBlue+2);    fitSlicesGaus(t,title[3],fit_fname[3],g_fname[3],"fit_"+ffoo[3]+to_string(j)+to_string(l),output, save);//,kBlue+2);    fitSlicesGaus(t_early,title[4],fit_fname[4],g_fname[4],"fit_"+ffoo[4]+to_string(j)+to_string(l), output, save);//,kBlue+2);    fitSlicesGaus(Ep_vs_x,title[6],fit_fname[6],g_fname[6],"fit_"+ffoo[6]+to_string(j)+to_string(l), output, save);    fitSlicesGaus(Ep_vs_y,title[7],fit_fname[7],g_fname[7],"fit_"+ffoo[7]+to_string(j)+to_string(l), output, save);  } // Fiducial Loop  output->Write();  output->Close();  // delete E, p, t, t_early, xy_calo;//input;  cout << "\nCreated: " << output_fname << endl;  cout << "\n-----------------End----------------" << endl;  return 0;}