// Code to make E/p vs E, E/p vs p, and E/p vs t plots// Using either gain corrected, or uncorrected// It also performs gauss fits in Y to slices in X// Sam Grant // March 2019// samuel.grant.18@ucl.ac.uk#include <iostream>#include "TFile.h"#include "TCanvas.h"#include "TH1D.h"#include "TH2D.h"#include "TStyle.h"#include "TAxis.h"#include "TProfile.h"#include "TF1.h"#include "TDirectory.h"#include "TObject.h"#include "TGraph.h"#include "TLegend.h"#include "TPaveStats.h"using namespace std;// Colour map plottervoid cmap(TH2D *hist, string title, string fname, TFile *output, bool save) {  //Clone to stop bugs   TH2D *hist_clone = (TH2D*)hist->Clone("hist_clone");  //Force the ranges to be sensible if ROOT's stupid autoscale fails like normal  double xmin = hist_clone->GetXaxis()->GetBinCenter( hist_clone -> FindFirstBinAbove(0,1) );  double xmax = hist_clone->GetXaxis()->GetBinCenter( hist_clone -> FindLastBinAbove(0,1) );  hist_clone->GetXaxis()->SetRangeUser(xmin,xmax);  double ymin = hist_clone->GetYaxis()->GetBinCenter( hist_clone -> FindFirstBinAbove(0,2) );  double ymax = hist_clone->GetYaxis()->GetBinCenter( hist_clone -> FindLastBinAbove(0,2) );  hist_clone->GetYaxis()->SetRangeUser(ymin,ymax);  hist_clone->SetName(fname.c_str());  TCanvas *c = new TCanvas("c", "c", 2000, 1000);  gStyle->SetPalette(55);    hist_clone->SetStats(0);  hist_clone->SetTitle(title.c_str());  c->SetRightMargin(0.13);  gStyle->SetPalette(55);   hist_clone->SetOption("COLZ");  //hist_clone->SetDirectory(output);  hist_clone->Draw();  if (save) {    c->SaveAs((fname+".png").c_str());   }  hist_clone->SetDirectory(output);  delete c;  return;}// Fit gaussians to slices along the x-axis and plot their meansvoid fitSlicesGaus(TH2D *hist, string title, string fname, string g_fname, TFile *output, bool save, int brd) {  //Clone to stop bugs   TH2D *hist_clone = (TH2D*)hist->Clone("hist_clone");  int nBins = hist_clone->GetNbinsX();   // Hardcode this to change the number of slices  int nSlices = nBins;  // Get the length of the slice in x  int length = nBins / nSlices;  hist_clone -> RebinX(length);  // Define the gaussian   TF1 *gFunc = new TF1("gFunc", "gaus");  // Declare step edges  int loStep;  int hiStep;  // Book a 1D hist for the Y projection slices  TH1D *projY;  // Book a 1D hist to take the results of each fit on it's X axis  TH1D *projX = hist_clone -> ProjectionX("prX");  // projX -> Rebin(length);  //projX -> Reset();  // Threshold (minimum number of bins), to define the fit range  double threshold;  double fitMin;  double fitMax;  double binCentre;  double centre;  // Slice loop  /*you could use Rebin2D(1, length) to rebin the 2D plot, then just make the projY between (i+1 and i+1) */  for(int i = 0 ; i < nSlices; i++) {    // Define steps    loStep = i+1;//*length;    hiStep = i+1;//*length;    // Perform projection    projY = hist_clone->ProjectionY("prY",loStep,hiStep);    // Clean up empty bins     if (projY->GetEntries() < 1) continue;    //Define the threshold, trying to avoid those tails. Half maximum works. Hacky but never mind.    threshold =  (projY -> GetBinContent(projY->GetMaximumBin()));// * (2/3);    threshold = threshold * 0.5;    // Define the fit range    fitMin = projY -> GetBinCenter(projY -> FindFirstBinAbove(threshold,1));    fitMax = projY -> GetBinCenter(projY -> FindLastBinAbove(threshold,1));    // "Q" : supress printing "M" use minuit to improve fit result    projY -> Fit(gFunc,"RQ","",fitMin,fitMax);     // Fill a histogram with the fit results    projX -> SetBinContent(i+1, gFunc->GetParameter(1));    projX -> SetBinError(i+1, gFunc->GetParError(1));    // Draw fits to slices, to see what's going on    // binCentre = projY->GetMaximumBin();    //  centre = projY->GetBinCenter(binCentre);    // gFunc->SetParameter(1,centre);   cout <<brd<<" "<<gFunc->GetParameter(1)<<" "<<gFunc->GetParError(1)<<endl;    TCanvas *c1 = new TCanvas();    //ksiourmen    // projY->SetStats(0);       ///gPad->Update();    projY->SetMarkerColor(kBlack);    projY->SetLineColor(kBlack);    // gPad->Update();    projY->Draw();     gStyle->SetOptFit();    projY->SetName((g_fname+"_"+to_string(i+1)).c_str());    projY->SetDirectory(output);    if (save) {      // c1->SaveAs((g_fname+to_string(i)+".png").c_str());    }    delete c1;    //  delete projY;  } // End slice loop  delete hist_clone;  //Force the ranges to be sensible if ROOT's autoscale fails  int binmin = projX->FindFirstBinAbove(0,1);  //  binmin = binmin - 1;  int binmax = projX->FindLastBinAbove(0,1);  //  double xmin = (projX -> GetBinCenter(binmin));;//projX->FindFirstBinAbove(0,1)-1) );  // double xmax = projX -> GetBinCenter(binmax);//projX->FindLastBinAbove(0,1)+1) );  // Draw the means, and compare with the profile   projX->SetName(fname.c_str());  // tpf->SetName("X Profile");   TCanvas *c2 = new TCanvas("c2", "c2", 2000, 1000);   projX->SetStats(0);  // gStyle->SetOptStat(0);    //projX->Draw();  projX->SetTitle(title.c_str());  projX->SetMarkerColor(kBlack);  projX->SetLineColor(kBlack);  projX->GetXaxis()->SetRange(binmin,binmax);//xmin,xmax);  projX->DrawCopy();  if (save) {    c2->SaveAs((fname+".png").c_str());  }   projX->SetDirectory(output);  delete c2;  // delete projY;  // delete projX;  //  delete gFunc;  return;}int main() {  // Take input for gain corrected or uncorrected  bool save = false;    string whichFile = "../uncorrected/t_by_xtal_normalised_brd.root";  string output_fname = "fits_t_by_xtal_normalised_brd.root";    TFile *input = TFile::Open(whichFile.c_str());  TFile *output = new TFile(output_fname.c_str(), "RECREATE");  cout << "Reading: " << whichFile << endl;  //Station loop  for (int m = 13 ; m < 20 ; m = m + 6 ) {     string stn = "St"+to_string(m)+"_";    string tstn = "St "+to_string(m)+" | ";    cout<<"stn_"<<m<<endl;    // Breaker board loop    for (int j = 1 ; j < 3 ; j++ ) {      string name;      // double yMin, yMax;      if (j == 1 ) { 	name = ", Short Lifetime";      }       else if ( j == 2 ) {     	name = ", Long Lifetime";	}        // Start fiducial region loop      for (int l = 0 ; l < 3 ; l++ ) {	string ffoo = "Ep_vs_t_early";	string tfoo = "E/p vs t";	string afoo = ";Fill Time [ns];E/p";		string fid;	// For titles	if (l == 0) {	  fid = "";	}	else if (l == 1 ) { 	  fid = ", Fiducial";	} 	else if (l == 2 ) { 	  fid = ", Non Fiducial";	}	// Put strings together for iterting hist/file/title names       	string h = stn+ffoo+"_"+to_string(l)+"_"+to_string(j);//+"_"+to_string(j);	string cmap_fname = stn+"cmap_"+ffoo+"_"+to_string(l)+"_"+to_string(j);//+"_"+to_string(j);	string fit_fname = stn+"fit_"+ffoo+"_"+to_string(l)+"_"+to_string(j);//+"_"+to_string(j);	string g_fname = stn+"gauss_"+ffoo+"_"+to_string(l)+"_"+to_string(j);//+"_"+to_string(j)+"_";	string title = tstn+tfoo+name+fid+afoo;	TH2D *Ep_vs_t_early = (TH2D*)input->Get(h.c_str());	if (Ep_vs_t_early == 0) continue;	Ep_vs_t_early->GetXaxis()->SetLimits(0,4.2*50);      	cmap(Ep_vs_t_early, title, cmap_fname, output, save);	fitSlicesGaus(Ep_vs_t_early, title, fit_fname, g_fname, output, save, j);      }    }  }    output->Write();  output->Close();  cout << "\nCreated: " << output_fname << endl;  return 0;}