// Code to make E/p vs E  plots// Sam Grant // March 2019// samuel.grant.18@ucl.ac.uk#include <iostream>#include "fitSlicesGauss.h"#include "cmap.h"#include "TFile.h"#include "TH1D.h"using namespace std;int main() {  // Take input for gain corrected or uncorrected  bool save = true;  bool scaled = false;  string in;  string whichFile = "../uncorrected2/demo_plots.root";  string output_fname = "demo_plots.root";  TFile *input = TFile::Open(whichFile.c_str());  TFile *output = new TFile(output_fname.c_str(), "recreate");  cout << "Reading: " << whichFile << endl;    // Start region loop      for (int l = 0 ; l < 3 ; l++ ) {	string ffoo[2] = {"E_vs_p","Ep_vs_t"};	string tfoo[2] = {"E vs p","E/p vs t"};	string afoo[2] = {";Track Momentum [GeV];Cluster Energy [GeV]",";In Fill Time [#mus];E/p"};	string h[2], cmap_fname[2], fit_fname[2], g_fname[2], title[2];	// Put strings together for iterting hist/file/title names	for (int i = 0 ; i < 2 ; i++ ) {	h[i] = ffoo[i]+"_"+to_string(l);//+"_"+to_string(j);	cmap_fname[i] = "cmap_"+ffoo[i]+"_"+to_string(l);//+"_"+to_string(j);	fit_fname[i] = "fit_"+ffoo[i]+"_"+to_string(l);//+"_"+to_string(j);	g_fname[i] = "gauss_"+ffoo[i]+"_"+to_string(l);//+"_"+to_string(j)+"_";	title[i] = tfoo[i]+afoo[i];      }	TH2D *E_vs_p = (TH2D*)input->Get(h[0].c_str());	E_vs_p->GetXaxis()->SetLimits(0,4);	E_vs_p->GetYaxis()->SetLimits(0,4);	TH2D *Ep_vs_t = (TH2D*)input->Get(h[1].c_str());	Ep_vs_t->GetXaxis()->SetLimits(0,4.2*50);	      	cmap(E_vs_p, title[0], cmap_fname[0], output, save);       	cmap(Ep_vs_t, title[1], cmap_fname[1], output, save);       }   output->Write();  output->Close();  cout << "\nCreated: " << output_fname << endl;  cout << "\n-----------------End----------------" << endl;  return 0;}