// Code to make E/p vs E  plots// Sam Grant // March 2019// samuel.grant.18@ucl.ac.uk#include <iostream>#include "fitSlicesGauss.h"#include "cmap.h"#include "TFile.h"#include "TH1D.h"#include "TLegend.h"#include "THistPainter.h"using namespace std;void draw(vector<TH1D *> hist_, vector<int> xtal_, string fname) {//, TH1D *hist2, string fname, string title) {    TCanvas *c = new TCanvas("c","c",1500,1000);  //  hist_.at(0)->Draw("PLC PMC");  for(int iPlot(0); iPlot < hist_.size(); iPlot++) {    cout<<xtal_.at(iPlot)<<endl;    if(xtal_.at(iPlot) < 18) {hist_.at(iPlot)->SetLineColor(kRed);}    if(xtal_.at(iPlot) > 17 && xtal_.at(iPlot) < 27) {hist_.at(iPlot)->SetLineColor(kGreen);}    if(xtal_.at(iPlot) > 26 && xtal_.at(iPlot) < 36) {hist_.at(iPlot)->SetLineColor(kOrange);}    if(xtal_.at(iPlot) > 35) {hist_.at(iPlot)->SetLineColor(kBlue);}    hist_.at(iPlot)->Scale(1./hist_.at(iPlot)->Integral());    hist_.at(iPlot)->SetStats(0);    hist_.at(iPlot)->Draw("SAME");  }	       //c->BuildLegend(0.1,0.7,0.38,0.9);//.31,.31,.89,.89);  //  c->BuildLegend(0.89,0.01,0.99,0.89);//.31,.31,.89,.89);  // hist_->SetTitle(title.c_str());  c->SaveAs(fname.c_str());  delete c;  return;  }int main() {  // Take input for gain corrected or uncorrected  bool save = true;   string whichFile = "../uncorrected2/energy_and_momentum_xtal2.root";    TFile *input = TFile::Open(whichFile.c_str());    cout << "Reading: " << whichFile << endl;  vector<TH1D *> E_;  vector<TH1D *> p_;  vector<int> EXtal_;  vector<int> pXtal_;  //Station loop  int counter = 0;  for (int stn(13); stn < 20; stn = stn + 6) {    for (int xtal(0); xtal < 54; xtal++) {      TH1D *E = (TH1D*)input->Get( ("St"+to_string(stn)+"_E_"+to_string(xtal)).c_str() );      TH1D *p = (TH1D*)input->Get( ("St"+to_string(stn)+"_p_"+to_string(xtal)).c_str() );      if(E!=0 && E->GetEntries() > 50000) {	E_.push_back(E);	EXtal_.push_back(xtal);      }      if(p!=0 && p->GetEntries() > 50000) {	p_.push_back(p);	pXtal_.push_back(xtal);      }    }  }  draw(E_,EXtal_,"ene_dists.png");  draw(p_,pXtal_,"mom_dists.png");  input->Close();  return 0;}